import React, { useEffect, useState } from 'react'
import { BrowserRouter as Router } from 'react-router-dom'
import { Badge, Button, Container, ListGroup, Form, Row, Col } from 'react-bootstrap'
import { useDispatch } from 'react-redux'
import FormContainer from './components/FormContainer'
import { info } from './actions/userActions'

import axios from 'axios'

const App = () => {

  const [nome, setNome] = useState('')
  const [rg, setRg] = useState('')
  const [cpf, setCpf] = useState('')
  const [dataNascimento, setDataNascimento] = useState('')
  const [dataAdmissao, setDataAdmissao] = useState('')
  const [funcao, setFuncao] = useState('')
  const [respApi, setRespApi] = useState([])

  const dispatch = useDispatch()

  const submitHandler = (e) => {
    e.preventDefault()
    dispatch(info({
      nome,
      rg,
      cpf,
      dataNascimento,
      dataAdmissao,
      funcao
    }))
  }

  useEffect(() => {
    const getPessoas = (e) => {
      axios.get('http://127.0.0.1:5000/api/read').then(resp => {
        setRespApi(resp.data.users);
      });
    }
    getPessoas()
  }, [])

  console.log(respApi);

  return (
    <Router>
      {/* <Header /> */}
      <main className='py-3'>
        <Container>
          <FormContainer>
            <h1>CRUD</h1>
            {/* {error && <Message variant='danger'>{error}</Message>} */}
            {/* {loading && <Loader />} */}
            <Form onSubmit={submitHandler}>
              <Form.Group controlId='nome'>
                <Form.Label>Nome:</Form.Label>
                <Form.Control
                  type='text'
                  placeholder='Victor Sousa'
                  // value={nome}
                  onChange={(e) => setNome(e.target.value)}
                ></Form.Control>
              </Form.Group>
              <Form.Group controlId='rg'>
                <Form.Label>RG </Form.Label>
                <Form.Control
                  type='text'
                  placeholder='RG'
                  onChange={(e) => setRg(e.target.value)}
                ></Form.Control>
              </Form.Group>
              <Form.Group controlId='cpf'>
                <Form.Label>CPF </Form.Label>
                <Form.Control
                  type='text'
                  placeholder='CPF'
                  onChange={(e) => setCpf(e.target.value)}
                ></Form.Control>
              </Form.Group>
              <Form.Group controlId='data_nascimento'>
                <Form.Label>Data Nascimento </Form.Label>
                <Form.Control
                  type='text'
                  placeholder='01/02/1990'
                  onChange={(e) => setDataNascimento(e.target.value)}
                ></Form.Control>
              </Form.Group>
              <Form.Group controlId='data_admissao'>
                <Form.Label>Data admissao </Form.Label>
                <Form.Control
                  type='text'
                  placeholder='01/02/2022'
                  onChange={(e) => setDataAdmissao(e.target.value)}
                ></Form.Control>
              </Form.Group>
              <Form.Group controlId='funcao'>
                <Form.Label>Funcao </Form.Label>
                <Form.Control
                  type='text'
                  placeholder='Desenvolvedor'
                  onChange={(e) => setFuncao(e.target.value)}
                ></Form.Control>
              </Form.Group>

              <Button type='submit' variant='primary'>
                Adicionar
              </Button>
            </Form>
            <Row className='py-3'>
              <Col>
                {respApi.map(element =>
                  <ListGroup key={element._id}>
                    <Badge className="mt-8">ID: {element._id} </Badge>
                    <ListGroup.Item><span>Nome: </span>{element.nome}<Button variant="light" onClick={() => alert('aqui')}>Editar</Button></ListGroup.Item>
                    <ListGroup.Item><span>Rg: </span>{element.rg}</ListGroup.Item>
                    <ListGroup.Item><span>Cpf: </span>{element.cpf}</ListGroup.Item>
                    <ListGroup.Item><span>Data Nascimento: </span>{element.dataNascimento}</ListGroup.Item>
                    <ListGroup.Item><span>Data Admissao: </span>{element.dataAdmissao}</ListGroup.Item>
                    <ListGroup.Item><span>Função: </span>{element.funcao}</ListGroup.Item>
                    <Button variant="danger">Excluir</Button>
                  </ListGroup>
                )}
              </Col>
            </Row>
          </FormContainer>
        </Container>
      </main>
      {/* <Footer /> */}
    </Router >
  )
}

export default App
