import React, { useState } from 'react'
import { BrowserRouter as Router, Route } from 'react-router-dom'
import { Container } from 'react-bootstrap'
import { Form, Button, Row, Col } from 'react-bootstrap'
import { useDispatch } from 'react-redux'

import FormContainer from '../components/FormContainer'
import { info } from './actions/userActions'

const App = () => {

  const [nome, setNome] = useState('')

  const dispatch = useDispatch()

  const submitHandler = (e) => {
    e.preventDefault()
    dispatch(info(nome))
  }


  return (
    <Router>
      {/* <Header /> */}
      <main className='py-3'>
        <Container>
          <FormContainer>
            <h1>CRUD</h1>
            {/* {error && <Message variant='danger'>{error}</Message>} */}
            {/* {loading && <Loader />} */}
            <Form onSubmit={submitHandler}>
              <Form.Group controlId='nome'>
                <Form.Label>Ensira um nome </Form.Label>
                <Form.Control
                  type='nome'
                  placeholder='Victor Sousa'
                  // value={nome}
                  onChange={(e) => setNome(e.target.value)}
                ></Form.Control>
              </Form.Group>
              <Form.Group controlId='rg'>
                <Form.Label>RG </Form.Label>
                <Form.Control
                  type='number'
                  placeholder='RG'
                // value={email}
                // onChange={(e) => setEmail(e.target.value)}
                ></Form.Control>
              </Form.Group>
              <Form.Group controlId='cpf'>
                <Form.Label>CPF </Form.Label>
                <Form.Control
                  type='number'
                  placeholder='CPF'
                // value={email}
                // onChange={(e) => setEmail(e.target.value)}
                ></Form.Control>
              </Form.Group>
              <Form.Group controlId='data_nascimento'>
                <Form.Label>Data Nascimento </Form.Label>
                <Form.Control
                  type='number'
                  placeholder='01/02/1990'
                // value={email}
                // onChange={(e) => setEmail(e.target.value)}
                ></Form.Control>
              </Form.Group>
              <Form.Group controlId='data_admissao'>
                <Form.Label>Data admissao </Form.Label>
                <Form.Control
                  type='number'
                  placeholder='01/02/2022'
                // value={email}
                // onChange={(e) => setEmail(e.target.value)}
                ></Form.Control>
              </Form.Group>
              <Form.Group controlId='funcao'>
                <Form.Label>Funcao </Form.Label>
                <Form.Control
                  type='funcao'
                  placeholder='Desenvolvedor'
                // value={email}
                // onChange={(e) => setEmail(e.target.value)}
                ></Form.Control>
              </Form.Group>

              <Button type='submit' variant='primary'>
                Adicionar
              </Button>
            </Form>

            <Row className='py-3'>
              <Col>
                Lista
              </Col>
            </Row>
          </FormContainer>
        </Container>
      </main>
      {/* <Footer /> */}
    </Router>
  )
}

export default App




